import React, { useState } from 'react'

import { ItemMenu } from './ItemMenu';

function sortMenu() {
	const [visibleMenu, setVisibleMenu] = useState(false);
	const dataMenu = [
		{ name: "Обучающее видео", id: 1 },
		{ name: "Оформление заказа", id: 2 },
		{ name: "Оплата", id: 3 },
		{ name: "Доставка", id: 4 },
		{ name: "Гарантия", id: 5 },
		{ name: "Возраст", id: 6 },
		{ name: "Контакты", id: 7 },
		{ name: "Партнерам", id: 8 }
	]

	const toggleMenu = () => {
		setVisibleMenu(!visibleMenu)
	}

	return (
		<div>
			<span
				className="btn-menu"
				onClick={toggleMenu}
			>
				X
			</span>
			{visibleMenu && <div className="header-menu">
				<ul className="menu">
					{dataMenu.map((item) => (
						<ItemMenu
							key={item.id}
							name={item.name}
						/>
					))}				
				</ul>
			</div>}
		</div>
	)
}

export default sortMenu
