import React, { useState, useRef } from "react";
import Slider from "react-slick";


export default class SliderDetailProduct extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nav1: null,
            nav2: null,
        };
    }

    componentDidMount() {
        this.setState({
            nav1: this.slider1,
            nav2: this.slider2
        });
    }
    
    

    render() {
            // ЭТОТ КОД НУЖЕН ЖДЯ ТОГО, ЧТО БЫ, ЕСЛИ СЛАЙДОВ МЕНЬШЕ ЧЕМ 5, ТО МЫ ОТКЛЮЧАЕМ КЛОНЫ, ЕСЛИ БОЛЬШЕ, ТО ВКЛЮЧАЕМ
            // количество отображаемых слайдов за раз у мини-слайдера
        let quantitySlide = 5
            // ищем все слайды с клонами у мини-слайдера
        let hideSlideClone = document.querySelectorAll('.slider-mimi_detail_product .slick-cloned');
            // если количество найденных слайдов меньше чем количество отображаемых слайдов, то мы удаляем у них клоны, если больше, то оставляем
        hideSlideClone.forEach((item) => {
            if (hideSlideClone.length <= quantitySlide) {
                return item.style = 'display: none'
            } else {
                return item.style = 'display: block'
            }
        });


        return (
            <>
                <div className="wrap-two-sliders">
                    <div className="slider-detail_product">
                        <Slider
                            asNavFor={this.state.nav2}
                            ref={slider => (this.slider1 = slider)}
                            arrows={false}
                            infinite={true}
                            speed={500}
                            slidesToShow={1}
                            slidesToScroll={1}
                            fade={true}
                            responsive={[
                                {
                                    breakpoint: 767,
                                    settings: {
                                        dots: true,
                                    }
                                },
                            ]}
                        >
                            {this.props.data.map((item, key) => {
                                return (
                                    <div key={key}>
                                        <div className="slide">
                                            <img src={item} />
                                        </div>
                                    </div>
                                )
                            })}

                        </Slider>
                    </div>

                    <div className="slider-mimi_detail_product">
                        <Slider
                            asNavFor={this.state.nav1}
                            ref={slider => (this.slider2 = slider)}
                            arrows={true}
                            infinite={true}
                            speed={500}
                            slidesToShow={quantitySlide}
                            slidesToScroll={1}
                            focusOnSelect={true}
                            vertical={true}
                            dots={false}
                            responsive={[
                                {
                                    breakpoint: 1279,
                                    settings: {
                                        vertical: false,
                                        arrows: true,
                                        slidesToShow: 3,
                                        centerMode: true,
                                        centerPadding: '5px',
                                    }
                                },
                                {
                                    breakpoint: 1023,
                                    settings: {
                                        vertical: false,
                                        arrows: true,
                                        slidesToShow: 3,
                                        centerMode: true,
                                        centerPadding: '5px',
                                    }
                                },
                            ]}
                        >
                            {this.props.data.map((item, key) => {
                                return (
                                    <div key={key}>
                                        <div className="slide-mini">
                                            <img src={item} />
                                        </div>
                                    </div>
                                )
                            })}
                        </Slider>
                    </div>
                </div>
            </>
        );
    }
}
