import React, { useState } from 'react'

function sortMenu() {
	const [visibleMenu, setVisibleMenu] = useState(false);
	const [menuItems, setMenuItems] = useState([
		{ name: "red" }
	])

	const toggleMenu = () => {
		setVisibleMenu(!visibleMenu)
	}

	return (
		<div>
			<span
				className="btn-menu"
				onClick={toggleMenu}
			>
				X
			</span>
			{visibleMenu && <div className="header-menu">
				<ul className="menu">
					<li className="menu-items">Обучающее видео</li>
					<li className="menu-items">Оформление заказа</li>
					<li className="menu-items">Оплата</li>
					<li className="menu-items">Доставка</li>
					<li className="menu-items">Гарантия</li>
					<li className="menu-items">Возраст</li>
					<li className="menu-items">Контакты</li>
					<li className="menu-items">Партнерам</li>
				</ul>
			</div>}
		</div>
	)
}

export default sortMenu
