import React, { useState } from 'react'

function Choice() {
  const [visibleMenu, setVisibleMenu] = useState(false);
  const dataMenu = [
    { name: "Вариант кухни", id: 1 },
    { name: "Размеры", id: 2 },
    { name: "Сенсор", id: 3 },
    { name: "Питающий кабель", id: 4 },
    { name: "Блок питания", id: 5 },
    { name: "Цвет свечения", id: 6 },
    { name: "Монтаж", id: 7 },
    { name: "Корзина", id: 8 }
  ]

  const toggleMenu = () => {
    setVisibleMenu(!visibleMenu)
  }

  return (
    <div>
      <span
          className="btn-menu"
          onClick={toggleMenu}
      >
      X
    </span>
      {visibleMenu && <div className="header-menu">
        <ul className="menu">
          {dataMenu.map(item => (
            <Item
              key={item.id}
              name={item.name}
            />
          ))}
        </ul>
      </div>}
    </div>
  )
}

export default Choice
